function myController(t){t.flagInput=function(n){t.input=n},t.$watch("celsius",function(n){"C"==t.input&&(t.fahrenheit=9*n/5+32,t.fahrenheit=+t.fahrenheit.toFixed(0))}),t.$watch("fahrenheit",function(n){"F"==t.input&&(t.celsius=5*(n-32)/9,t.celsius=+t.celsius.toFixed(1))}),t.$watch("meters",function(n){"m"==t.input&&(t.feet=3.2808399*n)}),t.$watch("ft",function(n){"ft"==t.input&&(t.meters=n/3.2808399)}),t.$watch("kg",function(n){"kg"==t.input&&(t.lbs=n/.454,t.lbs=+t.lbs.toFixed(3))}),t.$watch("lbs",function(n){"lbs"==t.input&&(t.kg=.454*n,t.kg=+t.kg.toFixed(3))}),t.$watch("hectares",function(n){"hectares"==t.input&&(t.acres=2.47105*n,t.acres=+t.acres.toFixed(5))}),t.$watch("acres",function(n){"acres"==t.input&&(t.hectares=n/2.47105,t.hectares=+t.hectares.toFixed(5))}),t.$watch("litres",function(n){"litres"==t.input&&(t.gallons=.264172*n,t.gallons=+t.gallons.toFixed(5))}),t.$watch("gallons",function(n){"gallons"==t.input&&(t.litres=n/.264172,t.litres=+t.litres.toFixed(5))}),t.$watch("kph",function(n){"kph"==t.input&&(t.mph=.621371*n,t.mph=+t.mph.toFixed(5))}),t.$watch("mph",function(n){"mph"==t.input&&(t.kph=n/.621371,t.kph=+t.kph.toFixed(5))})}angular.module("app",[]).controller("myController",myController);