function myController(t){t.flagInput=function(e){t.input=e},t.$watch("celsius",function(e){"C"==t.input&&(t.fahrenheit=9*e/5+32,t.fahrenheit=+t.fahrenheit.toFixed(0))}),t.$watch("fahrenheit",function(e){"F"==t.input&&(t.celsius=5*(e-32)/9,t.celsius=+t.celsius.toFixed(1))}),t.$watch("meters",function(e){"m"==t.input&&(t.feet=3.2808399*e,t.feet=+t.feet.toFixed(4))}),t.$watch("feet",function(e){"ft"==t.input&&(t.meters=e/3.2808399,t.meters=+t.meters.toFixed(4))}),t.$watch("kg",function(e){"kg"==t.input&&(t.lbs=e/.454,t.lbs=+t.lbs.toFixed(3))}),t.$watch("lbs",function(e){"lbs"==t.input&&(t.kg=.454*e,t.kg=+t.kg.toFixed(3))}),t.$watch("hectares",function(e){"hectares"==t.input&&(t.acres=2.47105*e,t.acres=+t.acres.toFixed(5))}),t.$watch("acres",function(e){"acres"==t.input&&(t.hectares=e/2.47105,t.hectares=+t.hectares.toFixed(5))}),t.$watch("litres",function(e){"litres"==t.input&&(t.gallons=.264172*e,t.gallons=+t.gallons.toFixed(5))}),t.$watch("gallons",function(e){"gallons"==t.input&&(t.litres=e/.264172,t.litres=+t.litres.toFixed(5))}),t.$watch("kph",function(e){"kph"==t.input&&(t.mph=.621371*e,t.mph=+t.mph.toFixed(5))}),t.$watch("mph",function(e){"mph"==t.input&&(t.kph=e/.621371,t.kph=+t.kph.toFixed(5))})}angular.module("app",[]).controller("myController",myController);